import React from "react";
import { useParams } from "react-router-dom";
import Header from "./Header";

export default function Car({Data}){
    const {ref} = useParams()
    const obj = Data.find(e=>e.ref===ref)
    return(
        <div className="container">
            <Header/>
            <div className='cars'>
                <div className="car">
                    <div style={{backgroundImage:`url('img/cars/car${obj.ref}.jpg')`}} className="img"></div>
                    <div className="text">
                        <span className='pro'>Name</span>
                        <span className='val'>{e.name}</span>
                        <span className='pro'>Company</span>
                        <span className='val'>{e.company}</span>
                        <span className='pro'>class</span>
                        <span className='val'>{e.class}</span>
                        <span className='pro'>description</span>
                        <span className='val'>{e.desc.slice(0,150)}...</span>
                    </div>
                </div>
            </div>
        </div>
    )
}
